back: "\ue611", // The optional back icon rendered for the node
font: "next-font", // The font family used for the node
color: "#006699", // The default node color
greyOut: "rgb(222, 222, 222)", // The grey out node color
highlight: "#f2ad4e", // The highlighted node color
param: "#666", // The color when a node is part of a parameter highlight
hover: "rgb(30, 203, 208)", // The hover over icon color
backColor: "#333", //The color of the optional back icon
interactionFront: true // The front icon is used for all interaction effects

